import React from 'react';
import logo from './logo.svg';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Table, Button, Container, Modal, ModalBody, ModalHeader, ModalFooter, FormGroup} from 'reactstrap';

const data = [
  { id: 1, personaje: "Naruto", anime: "Naruto"},
  { id: 2, personaje: "Goku", anime: "Dragon Ball"},
  { id: 3, personaje: "Kenichi", anime: "Kenichi"},
  { id: 4, personaje: "Ipo", anime: "Espíritu de Lucha"},
  { id: 5, personaje: "Logan", anime: "Wolverine"},
  { id: 6, personaje: "Baki", anime: "Baki"}
]

class App extends React.Component {
  state ={
    data:data
  }
  render (){
    return(
      <>
        <Container>
          <br/>
          <Button color="primary">Insertar Nuevo personaje</Button>
          <br/><br/>

          <Table>
            <thead><tr><th>Id</th>
            <th>Persona</th>
            <th>Anime</th>
            <th>Acciones</th></tr></thead>
            <tbody>
              {this.state.data.map((elemento)=>(
                <tr>
                  <td>{elemento.id}</td>
                  <td>{elemento.personaje}</td>
                  <td>{elemento.anime}</td>
                  <td><Button color='primary'>Editar</Button></td>{"  "}
                  <td><Button color='danger'>Eliminar</Button></td>
                </tr>

              ))}


            </tbody>
          </Table>


        </Container>
      </>
    ) 
    }
}

export default App;
