import React from 'react';
import logo from './logo.svg';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Table, Button, Container, Modal, ModalBody, ModalHeader, ModalFooter, FormGroup} from 'reactstrap';

const data = [
  { id: 1, personaje: "Naruto", anime: "Naruto"},
  { id: 2, personaje: "Goku", anime: "Dragon Ball"},
  { id: 3, personaje: "Kenichi", anime: "Kenichi"},
  { id: 4, personaje: "Ipo", anime: "Espíritu de Lucha"},
  { id: 5, personaje: "Logan", anime: "Wolverine"},
  { id: 6, personaje: "Baki", anime: "Baki"}
]

class App extends React.Component {
  state ={
    data:data,
    form:{
      id:'',
      personaje:'',
      anime:''
    },
    modalInsertar: false,
    modalEditar: false,
  };
handleChange=e=>{
  this.setState({
    form:{
      ...this.state.form,
      [e.target.name]: e.target.value,
    }
  });
}
mostrarModalInsertar=()=>{
  this.setState({modalInsertar:  true});
}
ocultarModalInsertar=()=>{
  this.setState({modalInsertar:  false});
}

insertar=()=>{
  var valorNuevo = {...this.state.form};
  valorNuevo.id=this.state.data.length+1;
  var lista=this.state.data;
  lista.push(valorNuevo);
  this.setState({data: lista, modalInsertar: false});
}

  render (){
    return(
      <>
        <Container>
          <br/>
          <Button color="success" onClick={()=>this.mostrarModalInsertar()}>Insertar Nuevo personaje</Button>
          <br/><br/>
          <Table>
            <thead><tr><th>Id</th>
            <th>Personaje</th>
            <th>Anime</th>
            <th>Acciones</th></tr></thead>
            <tbody>
              {this.state.data.map((elemento)=>(
                <tr>
                  <td>{elemento.id}</td>
                  <td>{elemento.personaje}</td>
                  <td>{elemento.anime}</td>
                  <td><Button color='primary'>Editar</Button></td>{"  "}
                  <td><Button color='danger'>Eliminar</Button></td>
                </tr>
              ))}             
            </tbody>
          </Table>          
        </Container>
        <Modal isOpen={this.state.modalInsertar}>
          <ModalHeader>
            <div><h3>Insertar Registro</h3></div>
          </ModalHeader>

          <ModalBody>
            <FormGroup>
              <label>Id:</label>
              <input className='form-control' readOnly type="text" value={this.state.data.length+1}/>
            </FormGroup>

            <FormGroup>
              <label>Personaje:</label>
              <input className='form-control' name="personaje"  type="text" onChange={this.handleChange}/>
            </FormGroup>

            <FormGroup>
              <label>Anime:</label>
              <input className='form-control' name='anime'  type="text" onChange={this.handleChange}/>
            </FormGroup>            
          </ModalBody>
          <ModalFooter>
            <Button color='primary' onClick={() => this.insertar()}>Insertar</Button>
            <Button color='danger' onClick={()=>this.ocultarModalInsertar()}>Cancelar</Button>
          </ModalFooter>


        </Modal>

        <Modal isOpen={this.state.modalEditar}>
          <ModalHeader>
            <div><h3>Editar Registro</h3></div>
          </ModalHeader>

          <ModalBody>
            <FormGroup>
              <label>Id:</label>
              <input className='form-control' readOnly type="text"/>
            </FormGroup>

            <FormGroup>
              <label>Personaje:</label>
              <input className='form-control' name="personaje"  type="text" onChange={this.handleChange}/>
            </FormGroup>

            <FormGroup>
              <label>Anime:</label>
              <input className='form-control' name='anime'  type="text" onChange={this.handleChange}/>
            </FormGroup>            
          </ModalBody>
          <ModalFooter>
            <Button color='primary' onClick={() => this.insertar()}>Editar</Button>
            <Button color='danger' onClick={()=>this.ocultarModalInsertar()}>Cancelar</Button>
          </ModalFooter>


        </Modal>

      </>
    ) 
    }
}

export default App;
